              </form>
            </div>
            <div className="col-sm-12">
              <span 
                style={{cursor:'pointer'}} 
                className={"span-border-bottom "}
                onClick={ (e) => {
                  setMode("login")
                }} >
                Back to login
              </span>
            </div>
          </div>
        );
      }

      case 'forgot':{
        return(
          <div>
            <form className="form-horizontal form-loanable">
              <div className="col-sm-12">
                <span className="form-control-feedback" aria-hidden="true">Email address</span>
