                </button>
              </div> */}
              <div className="col-sm-12">
                <span 
                style={{cursor:'pointer'}} 
                className={"span-border-bottom "}
                onClick={ (e) => {
                  setMode("login")
                }} >
                Back to login
              </span>
              </div>
            </form>
          </div>
        );
      }
    }
  }

  const contentFooter = () =>{
    switch(mode){
      case 'login':{

        return  <Modal.Footer>
                  <Button variant="secondary" onClick={()=>props.onClose()}>Close</Button>
