          setRegisterLoading(false)

        }
        break;
      }

      case 'forgot':{
        let _email = email.trim()
        if(isEmpty(_email)){
          onToast("error", "Email is empty.")
        }else if(!isEmailValid(_email)){
          onToast("error", "Email is Invalid.")
        }else {

          /*
          setForgotLoading(true)
          axios.post(`/api/v1/reset_password`, {
