            return <div />
        }

        if(item.images.length === 0 ){
            return <div />
        }


        let thumbnail = item.images.thumbnail
        let medium    = item.images.medium
        switch(thumbnail.length){
            case 0:{
              return(<div />)
            }
            case 1:{
                return(
                    <div key={item.id}> 
