                            <LazyLoadImage
                                alt={'image.alt'}
                                width="100%"
                                height="100px"
                                effect="blur"
                                src={thumbnail[1].url} />
                        </div>
                    </div>
                    <div class="hi-sub-container2">
                        <div class="hi-item3" onClick={()=>{ setIsOpen(true); setPhotoIndex(2); }} >
                            <LazyLoadImage
                                alt={'image.alt'}
                                width="100%"
                                height="100px"
                                effect="blur"
                                src={thumbnail[2].url} />
                        </div>
                    </div>
                </div>

                {
            isOpen && <Lightbox
                        mainSrc={medium[photoIndex].url}
                        nextSrc={medium[(photoIndex + 1) % medium.length].url}
                        prevSrc={medium[(photoIndex + medium.length - 1) % medium.length].url}

                        imageTitle= { (photoIndex + 1) + "/" + medium.length }
                        // mainSrcThumbnail={images[photoIndex]}
                        // nextSrcThumbnail={images[(photoIndex + 1) % images.length]}
                        // prevSrcThumbnail={images[(photoIndex + images.length - 1) % images.length]}

                        onCloseRequest={() => setIsOpen(false) }

                        onMovePrevRequest={() =>
                        // this.setState({
                        //     photoIndex: (photoIndex + images.length - 1) % images.length
                        // })
                            setPhotoIndex((photoIndex + medium.length - 1) % medium.length)
                        }
                        onMoveNextRequest={() =>
                        // this.setState({
                        //     photoIndex: (photoIndex + 1) % images.length
                        // })
                            setPhotoIndex((photoIndex + 1) % medium.length)
                        }
                    />
                } 
                </div>)

            }

            default:{
                return(<div key={item.id}> 
                            <div class="hi-container">
                            <div class="hi-sub-container1">
                                <div class="hi-item1" 
                                    onClick={()=>{ 
                                        setIsOpen(true); 
                                        setPhotoIndex(0);
                                    }} >
                                    <LazyLoadImage
                                        alt={'image.alt'}
                                        width="100%"
                                        height="100px"
                                        effect="blur"
                                        src={thumbnail[0].url} />
                                </div>
                                <div class="hi-item2" onClick={()=>{ setIsOpen(true); setPhotoIndex(1); }} >
                                    <LazyLoadImage
                                        alt={'image.alt'}
                                        width="100%"
                                        height="100px"
                                        effect="blur"
                                        src={thumbnail[1].url} />
                                </div>
                            </div>
                            <div class="hi-sub-container2">
                                <div class="hi-item3" onClick={()=>{ setIsOpen(true); setPhotoIndex(2); }} >
                                    <LazyLoadImage
                                        alt={'image.alt'}
                                        width="100%"
                                        height="100px"
                                        effect="blur"
                                        src={thumbnail[2].url} />
                                </div>
                            </div>
                        </div>

                        {
                    isOpen && <Lightbox
                                mainSrc={medium[photoIndex].url}
                                nextSrc={medium[(photoIndex + 1) % medium.length].url}
                                prevSrc={medium[(photoIndex + medium.length - 1) % medium.length].url}

                                imageTitle= { (photoIndex + 1) + "/" + medium.length }
                                // mainSrcThumbnail={images[photoIndex]}
                                // nextSrcThumbnail={images[(photoIndex + 1) % images.length]}
                                // prevSrcThumbnail={images[(photoIndex + images.length - 1) % images.length]}

                                onCloseRequest={() => setIsOpen(false) }

                                onMovePrevRequest={() =>
                                // this.setState({
                                //     photoIndex: (photoIndex + images.length - 1) % images.length
                                // })
                                    setPhotoIndex((photoIndex + medium.length - 1) % medium.length)
                                }
                                onMoveNextRequest={() =>
                                // this.setState({
                                //     photoIndex: (photoIndex + 1) % images.length
                                // })
                                    setPhotoIndex((photoIndex + 1) % medium.length)
                                }
                            />
                        } 
                        </div>)
            }
        }
      }

    // const items = () =>{
