import React, { useEffect, useState } from "react";
import Lightbox from "react-image-lightbox";
import MenuItem from "@material-ui/core/MenuItem";
import Menu from "@material-ui/core/Menu";
import MoreVertOutlinedIcon from '@material-ui/icons/MoreVertOutlined';
import VerifiedUserOutlinedIcon from '@material-ui/icons/VerifiedUserOutlined';
import { toast } from 'react-toastify';
import {CopyToClipboard} from 'react-copy-to-clipboard';
import { LazyLoadImage } from 'react-lazy-load-image-component';
// import ReactReadMoreReadLess from "react-read-more-read-less";
import moment from "moment";

import parse from 'html-react-parser';

import ReportDialog from './ReportDialog'
import { isEmpty, commaFormatted, ReadMore } from "../utils";

var _ = require('lodash');

const UseHomeItem = (props) => {
  const [item, setItem] = useState({});
  const [anchorEl, setAnchorEl] = useState(null);
  const [isOpen, setIsOpen] = useState(false);
  const [isOpenMenu, setIsOpenMenu] = useState(false);
  const [photoIndex, setPhotoIndex] = useState(0);

  const [followUp, setFollowUp] = useState(false);

  const [showModalReport, setShowModalReport] = useState(false);

  useEffect(() => {

    console.log('UseHomeItem : ', props.user.uid)
    setItem(props.item)
    // 
    setFollowUp( _.isEmpty( (props.my_follows.find((el)=>el.nid === parseInt(item.nid) && el.status)) ) ? false : true )
  });

  const handleClose = () => {
    setAnchorEl(null);
  };

  const handleClick = (event) => {

    console.log('setAnchorEl : ', event.currentTarget)
    console.log(typeof event.currentTarget);
    setAnchorEl(event.currentTarget);
  };

  const itemView = () =>{
    // console.log('itemView :', item)

    if(Object.keys(item).length === 0 ){
        return <div />
    }


    if(isEmpty(item.images)){
        return <div />
    }


    let thumbnail = item.images[0]
    let medium    = item.images[1]

    // thumbnail = [{url:'https://scontent.fbkk5-1.fna.fbcdn.net/v/t1.6435-9/215541769_468809007538018_7514224006994884957_n.jpg?_nc_cat=109&ccb=1-3&_nc_sid=730e14&_nc_eui2=AeFpOyNqlQtdgtwQ8dNA_0sYjbI7gtXtJNaNsjuC1e0k1pVNF88_vEsFfEh6D5g2kkzCX0OZ2RUBIRsOcnB8BWWs&_nc_ohc=IAIr6v2N5woAX91kaO2&_nc_ht=scontent.fbkk5-1.fna&oh=f74a0901be43ddb1f0f66c6ab5307722&oe=60EDC748'}]
    // medium = [{url:'https://scontent.fbkk5-1.fna.fbcdn.net/v/t1.6435-9/215541769_468809007538018_7514224006994884957_n.jpg?_nc_cat=109&ccb=1-3&_nc_sid=730e14&_nc_eui2=AeFpOyNqlQtdgtwQ8dNA_0sYjbI7gtXtJNaNsjuC1e0k1pVNF88_vEsFfEh6D5g2kkzCX0OZ2RUBIRsOcnB8BWWs&_nc_ohc=IAIr6v2N5woAX91kaO2&_nc_ht=scontent.fbkk5-1.fna&oh=f74a0901be43ddb1f0f66c6ab5307722&oe=60EDC748'}]

    switch(thumbnail.length){
        case 0:{
          return(<div />)
        }
        case 1:{
          return(
              <div key={item.nid}> 
                  <div class="hi-container">
                      <div class="hi-sub-container1">
                          <div class="hi-item1" 
                              onClick={()=>{ 
                                  setIsOpen(true); 
                                  setPhotoIndex(0);
                              }} >
                              <LazyLoadImage
                                  alt={'image.alt'}
                                  width="100%"
                                  height="100px"
                                  effect="blur"
                                  src={thumbnail[0].url} />
                          </div>
                      </div>
                  </div>
                  {
                  isOpen && <Lightbox
                          mainSrc={medium[photoIndex].url}
                          nextSrc={medium[(photoIndex + 1) % medium.length].url}
                          prevSrc={medium[(photoIndex + medium.length - 1) % medium.length].url}

                          imageTitle= { (photoIndex + 1) + "/" + medium.length }
                          // mainSrcThumbnail={images[photoIndex]}
                          // nextSrcThumbnail={images[(photoIndex + 1) % images.length]}
                          // prevSrcThumbnail={images[(photoIndex + images.length - 1) % images.length]}

                          onCloseRequest={() => setIsOpen(false) }

                          onMovePrevRequest={() =>
                          // this.setState({
                          //     photoIndex: (photoIndex + images.length - 1) % images.length
                          // })
                              setPhotoIndex((photoIndex + medium.length - 1) % medium.length)
                          }
                          onMoveNextRequest={() =>
                          // this.setState({
                          //     photoIndex: (photoIndex + 1) % images.length
                          // })
                              setPhotoIndex((photoIndex + 1) % medium.length)
                          }
                      />
                  } 
              </div>
          )
        }

        case 2:{
            return(<div key={item.nid}> 
                <div class="hi-container">
                <div class="hi-sub-container1">
                    <div class="hi-item1" 
                        onClick={()=>{ 
                            setIsOpen(true); 
                            setPhotoIndex(0);
                        }} >
                        <LazyLoadImage
                            alt={'image.alt'}
                            width="100%"
                            height="100px"
                            effect="blur"
                            src={thumbnail[0].url} />
                    </div>
                    <div class="hi-item2" onClick={()=>{ setIsOpen(true); setPhotoIndex(1); }} >
                        <LazyLoadImage
                            alt={'image.alt'}
                            width="100%"
                            height="100px"
                            effect="blur"
                            src={thumbnail[1].url} />
                    </div>
                </div>
            </div>

            {
        isOpen && <Lightbox
                    mainSrc={medium[photoIndex].url}
                    nextSrc={medium[(photoIndex + 1) % medium.length].url}
                    prevSrc={medium[(photoIndex + medium.length - 1) % medium.length].url}

                    imageTitle= { (photoIndex + 1) + "/" + medium.length }
                    // mainSrcThumbnail={images[photoIndex]}
                    // nextSrcThumbnail={images[(photoIndex + 1) % images.length]}
                    // prevSrcThumbnail={images[(photoIndex + images.length - 1) % images.length]}

                    onCloseRequest={() => setIsOpen(false) }

                    onMovePrevRequest={() =>
                    // this.setState({
                    //     photoIndex: (photoIndex + images.length - 1) % images.length
                    // })
                        setPhotoIndex((photoIndex + medium.length - 1) % medium.length)
                    }
                    onMoveNextRequest={() =>
                    // this.setState({
                    //     photoIndex: (photoIndex + 1) % images.length
                    // })
                        setPhotoIndex((photoIndex + 1) % medium.length)
                    }
                />
            } 
            </div>)

        }

        case 3:{
            return(<div key={item.nid}> 
                <div class="hi-container">
                <div class="hi-sub-container1">
                    <div class="hi-item1" 
                        onClick={()=>{ 
                            setIsOpen(true); 
                            setPhotoIndex(0);
                        }} >
                        <LazyLoadImage
                            alt={'image.alt'}
                            width="100%"
                            height="100px"
                            effect="blur"
                            src={thumbnail[0].url} />
                    </div>
                    <div class="hi-item2" onClick={()=>{ setIsOpen(true); setPhotoIndex(1); }} >
                        <LazyLoadImage
                            alt={'image.alt'}
                            width="100%"
                            height="100px"
                            effect="blur"
                            src={thumbnail[1].url} />
                    </div>
                </div>
                <div class="hi-sub-container2">
                    <div class="hi-item3" onClick={()=>{ setIsOpen(true); setPhotoIndex(2); }} >
                        <LazyLoadImage
                            alt={'image.alt'}
                            width="100%"
                            height="100px"
                            effect="blur"
                            src={thumbnail[2].url} />
                    </div>
                </div>
            </div>

            {
        isOpen && <Lightbox
                    mainSrc={medium[photoIndex].url}
                    nextSrc={medium[(photoIndex + 1) % medium.length].url}
                    prevSrc={medium[(photoIndex + medium.length - 1) % medium.length].url}

                    imageTitle= { (photoIndex + 1) + "/" + medium.length }
                    // mainSrcThumbnail={images[photoIndex]}
                    // nextSrcThumbnail={images[(photoIndex + 1) % images.length]}
                    // prevSrcThumbnail={images[(photoIndex + images.length - 1) % images.length]}

                    onCloseRequest={() => setIsOpen(false) }

                    onMovePrevRequest={() =>
                    // this.setState({
                    //     photoIndex: (photoIndex + images.length - 1) % images.length
                    // })
                        setPhotoIndex((photoIndex + medium.length - 1) % medium.length)
                    }
                    onMoveNextRequest={() =>
                    // this.setState({
                    //     photoIndex: (photoIndex + 1) % images.length
                    // })
                        setPhotoIndex((photoIndex + 1) % medium.length)
                    }
                />
            } 
            </div>)

        }

        default:{
            return(<div key={item.nid}> 
                        <div class="hi-container">
                        <div class="hi-sub-container1">
                            <div class="hi-item1" 
                                onClick={()=>{ 
                                    setIsOpen(true); 
                                    setPhotoIndex(0);
                                }} >
                                <LazyLoadImage
                                    alt={'image.alt'}
                                    width="100%"
                                    height="100px"
                                    effect="blur"
                                    src={thumbnail[0].url} />
                            </div>
                            <div class="hi-item2" onClick={()=>{ setIsOpen(true); setPhotoIndex(1); }} >
                                <LazyLoadImage
                                    alt={'image.alt'}
                                    width="100%"
                                    height="100px"
                                    effect="blur"
                                    src={thumbnail[1].url} />
                            </div>
                        </div>
                        <div class="hi-sub-container2">
                            <div class="hi-item3" onClick={()=>{ setIsOpen(true); setPhotoIndex(2); }} >
                                <LazyLoadImage
                                    alt={'image.alt'}
                                    width="100%"
                                    height="100px"
                                    effect="blur"
                                    src={thumbnail[2].url} />
                            </div>
                        </div>
                    </div>

                    {
                isOpen && <Lightbox
                            mainSrc={medium[photoIndex].url}
                            nextSrc={medium[(photoIndex + 1) % medium.length].url}
                            prevSrc={medium[(photoIndex + medium.length - 1) % medium.length].url}

                            imageTitle= { (photoIndex + 1) + "/" + medium.length }
                            // mainSrcThumbnail={images[photoIndex]}
                            // nextSrcThumbnail={images[(photoIndex + 1) % images.length]}
                            // prevSrcThumbnail={images[(photoIndex + images.length - 1) % images.length]}

                            onCloseRequest={() => setIsOpen(false) }

                            onMovePrevRequest={() =>
                            // this.setState({
                            //     photoIndex: (photoIndex + images.length - 1) % images.length
                            // })
                                setPhotoIndex((photoIndex + medium.length - 1) % medium.length)
                            }
                            onMoveNextRequest={() =>
                            // this.setState({
                            //     photoIndex: (photoIndex + 1) % images.length
                            // })
                                setPhotoIndex((photoIndex + 1) % medium.length)
                            }
                        />
                    } 
                    </div>)
        }
    }
  }

  const menu = () =>{
    return  <Menu
              keepMounted
              anchorEl={anchorEl}
              onClose={handleClose}
              open={Boolean(anchorEl)}>
              <CopyToClipboard text={"http://localhost:8099/detail/" + item.nid}>
                <MenuItem onClick={()=>{

                  toast.info("Link to post copied to clipboard.", 
                  {
                      position: "bottom-right", 
                      hideProgressBar: true,
                      autoClose: 1000,
                  }) 
                  handleClose()
                }}>Copy link</MenuItem>
              </CopyToClipboard>
              <MenuItem onClick={()=>{
                    if(isEmpty(props.user)){
                      props.updateState({showModalLogin: true})
                    }else{
                      // props.updateState({showModalReport: true})

                      setShowModalReport(true)
                    }

                    handleClose()
                  }}>Report</MenuItem>
            </Menu> 
  }

  const count_app_followers = () =>{
      return (_.filter(item.app_followers, (o) => { return o.status; })).length
  }

  const is_follows = () =>{
    console.log('is_follows :', props)
    if(!_.isEmpty(props.user)){
      return _.isEmpty( (props.my_follows.find((el)=>el.nid === parseInt(item.nid) && el.status)) ) ? "gray" : "red"
    }

    return "gray"
  }

  return (
    <div key={item.nid} style={{margin: 10}}>  
      {itemView()}
      <div style={{cursor: 'pointer'}} onClick={()=>{
        // console.log('/detail/:id : ', props)
        // /detail/:id
        // props.history.push({pathname: `detail/${item.id}`, state: { item } })
      }}> 
          <div>
              <div style={{cursor: 'pointer'}} onClick={()=>{
                props.history.push({pathname: `detail/${item.nid}`, key: item.nid, state: { item } })
              }}> 

              <div>
                <div>-: {item.name_surname}</div>
              </div>

              <div>
                <div>/: {item.title} -- {item.nid}</div>
              </div>
              <div>
                <div>: {!isEmpty(item.transfer_amount) ? commaFormatted(item.transfer_amount) : item.transfer_amount}</div>
              </div>
              <div>
                <div>: {moment(item.transfer_date).format('MMM DD, YYYY')}</div>
              </div>
            </div>
            <div>
              <div></div>
              <div style={{maxWidth:"300px"}}>
                {/* {
                  !isEmpty(item.detail) && 
                  <ReactReadMoreReadLess
                    charLimit={50}
                    readMoreText={"Read more"}
                    readLessText={"Read less"}
                    readMoreClassName="read-more-less--more"
                    readLessClassName="read-more-less--less"
                    onClick={()=>console.log("ReactReadMoreReadLess")}
                  >
                    {item.detail}
                  </ReactReadMoreReadLess>
                } */}

                {
                  !isEmpty(item.detail) && <ReadMore>{parse(item.detail)}</ReadMore>
                }

              </div>
            </div> 
          </div>
          <div>
            <VerifiedUserOutlinedIcon 
              style={{fill:  is_follows() }}
              onClick={()=>{ 
                // toast.info("Wow so easy!", 
                //           {
                //             position: "bottom-right", 
                //             hideProgressBar: true,
                //             autoClose: 1000,
                //           }) 

                console.log("props.user : ", props.user)

                if(_.isEmpty(props.user)){
                  props.updateState({showModalLogin: true})
                }else{

                  let f = props.my_follows.find((o)=> o.nid === item.nid)

                  let status = true
                  if(!_.isEmpty(f)){
                    status = !f.status
                  }

                  props.myFollow({uid: props.user.uid, nid: item.nid, status})

                }

                /*
                let cL = this.props.user
                if(isEmpty(cL)){
                  _this.props.onUpdateState({showModalLogin: true})
                }else{

                  let follow_up = true;
                  if(!isEmpty(___follow_ups)){
                    let find_fup = ___follow_ups.find(value => String(value.id) === String(item.id) )
                    // console.log('fup : ', find_fup, item.id)

                    if(!isEmpty(find_fup)){
                      follow_up = !find_fup.follow_up
                    }
                  }

                  if(follow_up){
                    _this.props.toast.show("Follow up");
                  }else{
                    _this.props.toast.show("Unfollow up");
                  }

                  ___followUp({"id": item.id, 
                              "local": true, 
                              "follow_up": follow_up, 
                              "unique_id": getUniqueId(), 
                              "owner_id": item.owner_id, 
                              "date": Date.now()}, 0);
                }
                */
              }} />
            <MoreVertOutlinedIcon onClick={handleClick} />
            <div onClick={()=>{
              props.history.push({pathname: `my-follower/${item.nid}`, key: item.nid, state: { item } })
            }}>
              { count_app_followers() } follower
            </div>
          </div>
      </div>
      {menu()}  

      { showModalReport && <ReportDialog {...props} showModal={showModalReport} onClose = {()=>{  setShowModalReport(false) }}  /> }
    </div>
  );
};

export default UseHomeItem;
