import { ADD_CONTENTS_DATA, CLEAR_ALL_CONTENTS_DATA } from '../constants';

var _ = require('lodash');

var merge = (a, b, p) => a.filter(aa =>!b.find(bb => aa[p] === bb[p]));
const initialState = {
  data: []
}

const app = (state = initialState, action) => {
  switch (action.type) {
    case ADD_CONTENTS_DATA:{
      console.log('app : ', state.data, action.data, merge(state.data, action.data, 'nid'))

      return { ...state, data: merge(state.data, action.data, 'nid')}
    }

    case CLEAR_ALL_CONTENTS_DATA:{
      return initialState
    }

    default:
      return state;
  }
}

export default app
