import Ajv from "ajv"
import { FETCH_DATA } from '../constants';

const _fetchData = data => ({
  type: FETCH_DATA,
  data,
});

export const fetchData = (data) => dispatch => {
  var innerSchema = {
    "type" : "object",
    "properties" : {
      name: {type: "string"},
      pass: {type: "string"},
      email: {type: "integer"}
    },
    "required" : ["name"]
  }


  //----------------
  const schema = {
    type: "array",
    items: innerSchema,
    additionalProperties: false,
  }

  const ajv = new Ajv() 
  const validate = ajv.compile(schema)

  let data = [{name: 'd', pass: 'e', email: 1}, {name: 'd', pass: 'e', email: "1000"}]

  console.log("validate(data) : >> ", validate(data) )

  dispatch(_fetchData(data));
}
